import { BadRequestException, Injectable } from '@nestjs/common';
import { BasicService } from '../../shared/services/basic-service.service';
import { DocumentTypeEnum, Kyc, KycStatus } from './entities/kyc.entity';
import { Repository } from 'typeorm';
import { InjectRepository } from '@nestjs/typeorm';
import { Helper } from '../../shared/helpers';
import { RequestController } from '../../shared/core';
import env from '../../config/env.config';
import { CreateKycDto } from './dto/kyc.dto';
import { User } from '../users/entities/user.entity';
import { isDev } from '../../environment/isDev';

@Injectable()
export class KycService extends BasicService<Kyc> {
  constructor(
    @InjectRepository(Kyc)
    private kycRepository: Repository<Kyc>,
    private readonly requestController: RequestController,
  ) {
    super(kycRepository, 'Kyc');
  }

  async createOrUpdateKyc(userId: string, createKycDto: CreateKycDto) {
    const kyc = await this.findOne(userId, 'userId', null, false);
    if (kyc) {
      return this.update(kyc.id, createKycDto);
    }
    return this.create({ ...createKycDto, userId });
  }

  async getMetadata() {
    return {
      documentType: Object.values(DocumentTypeEnum).map((value) => ({ value, label: Helper.toSentenceCase(Object.keys(DocumentTypeEnum).find((key) => DocumentTypeEnum[key] === value)) })),
    };
  }

  async verifyDocument(document: CreateKycDto, user: User) {
    const headers = this.getHeaders();
    const body = {
      doc_type: isDev() ? 'PP' : document.documentType,
      doc_image: isDev() ? '' : document.documentUrl,
      doc_country: isDev() ? 'GBR' : document.country,
    };
    const response = await this.requestController.post<any, typeof body>(
      env.prembly.baseUrl + '/identitypass/verification/document',
      {},
      body,
      { headers },
    );
    if(response.status && response.verification.status === 'VERIFIED') {
      const result = await this.createOrUpdateKyc(user.id, { ...document, status: KycStatus.APPROVED });
      console.log({result});
      user.kycVerified = true;
      await user.save();
      return result;
    } else {
      await this.createOrUpdateKyc(user.id, { ...document, status: KycStatus.REJECTED });
      throw new BadRequestException('Document verification failed');
    }
    // this.createOrUpdateKyc(user.id, { ...document, status: response.status });
  }

  private getHeaders(): Record<string, string> {
    return {
      'Content-Type': 'application/json',
      'x-api-key': env.prembly.apiKey,
      'app-id': env.prembly.appId,
    };
  }
}
